mapper.select:
select m.*,d.cname, d.ctitle, d.ccontent, d.cfreecontent, d.ilanguageid, d.cimageurl, d.igroupid from t_shipping_method m inner join t_shipping_method_detail d on d.ccode = m.ccode where m.istorageid = ${storageId} and (m.ccountrys like '%${country}%' or m.ccountrys is null) and (${weight} < m.iendweight and ${weight} >= m.istartweight) and d.ilanguageid = #{lang} and m.benabled = true and (#{price} < m.fendprice and #{price} >= m.fbeginprice)
and m.bisspecial = #{isSpecial}
order by iid

select m.*,d.cname, d.ctitle, d.ccontent, d.cfreecontent, d.ilanguageid, d.cimageurl, d.igroupid from t_shipping_method m inner join t_shipping_method_detail d on d.ccode = m.ccode where m.istorageid in #{item} and (m.ccountrys like '%${country}%' or m.ccountrys is null) and (${weight} < m.iendweight and ${weight} >= m.istartweight) and d.ilanguageid = #{lang} and m.benabled = true and (#{price} < m.fendprice and #{price} >= m.fbeginprice)
and m.bisspecial = #{isSpecial}
order by iid
